/**
 * After a group or user has been added to a Cohesity Cluster,
 * the referenced Idp principal can be used by the Cohesity Cluster.
 * In addition, this operation maps Cohesity roles with a group or user and
 * this mapping defines the privileges allowed on the Cohesity Cluster for the
 * group or user.
 * For example if an 'management' group is created on the Cohesity Cluster
 * for the Idp 'management' principal group and is associated with the
 * Cohesity 'View' role, all users in the referenced Idp
 * 'management' principal group can log in to the Cohesity Dashboard but
 * will only have view-only privileges. These users cannot create new
 * Protection Jobs, Policies, Views, etc.
 * NOTE: Local Cohesity users and groups cannot be created by this operation.
 * Local Cohesity users or groups do not have an associated Idp
 * principals and are created directly in the default LOCAL domain.
 * @return	Returns the []*models.AddedIdpPrincipal response from the API call
 */
func (me *IDPS_IMPL) AddActiveIdpPrincipals () ([]*models.AddedIdpPrincipal, error) {
    //the endpoint path uri
    _pathUrl := "/public/idp/principals"

    //variable to hold errors
    var err error = nil
    //the base uri for api requests
    _queryBuilder := configuration.GetBaseURI(configuration.DEFAULT_HOST,me.config);

    //prepare query string for API call
   _queryBuilder = _queryBuilder + _pathUrl

    //validate and preprocess url
    _queryBuilder, err = apihelper.CleanUrl(_queryBuilder)
    if err != nil {
        //error in url validation or cleaning
        return nil, err
    }
     if me.config.AccessToken() == nil {
        return nil, errors.New("Access Token not set. Please authorize the client using client.Authorize()");
    }
    //prepare headers for the outgoing request
    headers := map[string]interface{} {
        "user-agent" : "cohesity-Go-sdk-1.1.2",
        "accept" : "application/json",
        "Authorization" : fmt.Sprintf("%s %s",*me.config.AccessToken().TokenType, *me.config.AccessToken().AccessToken),
    }

    //prepare API request
    _request := unirest.Post(_queryBuilder, headers, nil)
    //and invoke the API call request to fetch the response
    _response, err := unirest.AsString(_request,me.config.SkipSSL());
    if err != nil {
        //error in API invocation
        return nil, err
    }

    //error handling using HTTP status codes
    if (_response.Code == 0) {
        err = apihelper.NewAPIError("Error", _response.Code, _response.RawBody)
    } else if (_response.Code < 200) || (_response.Code > 206) { //[200,206] = HTTP OK
            err = apihelper.NewAPIError("HTTP Response Not OK", _response.Code, _response.RawBody)
    }
    if(err != nil) {
        //error detected in status code validation
        return nil, err
    }

    //returning the response
    var retVal []*models.AddedIdpPrincipal
    err = json.Unmarshal(_response.RawBody, &retVal)

    if err != nil {
        //error in parsing
        return nil, err
    }
    return retVal, nil

}

